import React, { useEffect, useState } from 'react';
import csvdata from '../../data/restaurants.csv';
import Cardcontainer from '../Cardcontainer/cardcontainer';
import classes from './restaurant.module.css';

const Restaurant = (props) => {
    const [data,setData] = useState([]);

    useEffect(() => {
        getcsvData();
    },[])
    async function getcsvData() {
        const arr = [];
        const response = await fetch(csvdata);
        const resdata = await response.text();
        const rowdata = resdata.split(/\n/).slice(1);
        rowdata.forEach(row => {
            const obj = {};
            const threedata = row.split(/"/);
            if(threedata.length === 1) {
                const val = threedata[0].split(/,/)
                obj.id = val[0];
                obj.name = val[1];
                obj.cuisines = val[2];
                obj.averagecost = val[3];
                obj.currency = val[4];
                obj.tablebooking = val[5];
                obj.onlinedelivery = val[6];
                obj.rating = val[7];
                obj.ratingcolor = val[8];
                obj.ratingtext = val[9];
                obj.votes = val[10];
            }
            else {
                const val1 = threedata[0].split(/,/)
                const val2 = threedata[2].split(/,/)
                obj.id = val1[0];
                obj.name = val1[1];
                obj.cuisines = threedata[1];
                obj.averagecost = val2[1];
                obj.currency = val2[2];
                obj.tablebooking = val2[3];
                obj.onlinedelivery = val2[4];
                obj.rating = val2[5];
                obj.ratingcolor = val2[6];
                obj.ratingtext = val2[7];
                obj.votes = val2[8];
            }
            arr.push(obj);
        })
        setData(arr);
    }

    const search = (event) => {
        const datas = [...data];
        const val = event.target.value;
        const v=datas.filter(d => d.name.toLowerCase().includes(val));
        console.log(v)
        // if(v.length===0) setData(datas);
        // else setData(v)
    }
    return (
        <div> 
            <div className={classes.Search}><input className={classes.Input}type="text" placeholder="Search" onChange={search}/></div>
            <Cardcontainer datas={data}/>
        </div>
    )
}

export default Restaurant;
